import { useState } from "react"
import { useParams } from "react-router-dom"

const RegisterMessage = () => {
    const [data, setData] = useState({
        sms_msg: '',
        status: ''
    })
    const { id } = useParams()

    const handleChange = (e) => {
        const { name, value } = e.target

        setData(data => ({
            ...data,
            [name]: value
        }))
    }

    const createMessage = async (e) => {
        e.preventDefault()

        try {
            const res = await fetch(`${process.env.REACT_APP_SERVERURL}/update-status/${id}`, {
                method: "POST",
                headers: {
                    "Content-Type":"application/json"
                },
                body: JSON.stringify(data)
            })
            if(res.status === 200) {
                navigate(`/register/${id}/message`)
                toast.success('Created Successfully')
            }
        } catch (err) {
            console.log(err)
        }
    }

    return(
        <section className="row">
            <div className="col-md-6 mx-auto">
                <h1 className="text-center mt-2">Register Message</h1>
                <form onSubmit={createMessage}>
                    <div className="form-group">
                    <label htmlFor="msg_phone">Message</label>
                    <textarea
                    value={data.sms_msg}
                    name="msg_phone"
                    id="msg_phone"
                    className="form-control"
                    placeholder="Type your message"
                    onChange={handleChange}
                    />
                    </div>
                    <div className="form-group">
                        <label htmlFor="status">Status</label>
                        <select name="status" id="status" className="form-control" value={data.status} onChange={handleChange}>
                            <option defaultChecked>Type</option>
                            <option value="enviado">Enviado</option>
                            <option value="recebido">Recebido</option>
                            <option value="erro de envio">Erro de Envio</option>
                        </select>
                    </div>
                    <button className="btn btn-dark" type="submit">Create</button>
                </form>
            </div>
        </section>
    )
}

export default RegisterMessage
