import { useState, useEffect, useMemo } from "react"
import MessageList from "../../component/MessageList"
import { Link, useNavigate } from "react-router-dom"

const Home = () => {
    const [data, setData] = useState()
    const navigate = useNavigate()
    const [loading, setLoading] = useState(false)

    useEffect(() => {

        const getData = async () => {
            try {
                const res = await fetch(`${process.env.REACT_APP_SERVERURL}/message`)
                const json = await res.json()
                setLoading(true)
                setData(json.messages)
            } catch (err) {
                console.log(err)
            }
        }
        getData()

    }, [])

    const cachedMessages = useMemo(() => data, [data])

    const deletePhone =  async (id) => {
        try {
            const res = await fetch(`${process.env.REACT_APP_SERVERURL}/message/${id}`, {
                method: "DELETE",
                headers: {
                    "Content-Type":"application/json"
                },
                body: JSON.stringify({ id })
            })

           if(res.status === 200) {
               navigate(`/`, { replace: true })
               alert('Deleted Successfully.')
           }
        } catch (err) {
            console.log(err)
        }
    }

    if(!loading) {
        return <h3 className="mt-2 text-center">Loading...</h3>
    }

    return(
       <div className="responsive-table">

        <Link to='/register'>
            <button className="btn btn-dark mt-2">Register</button>
        </Link>
        <table className="table table-stripped mt-2">
            <thead className="table-dark">
                <tr>
                    <th>Celular</th>
                    <th>Status</th>
                    <th>#</th>
                    <th>#</th>
                </tr>
            </thead>
            <tbody>              
                {cachedMessages?.map((msg) => 
                <MessageList 
                key={msg.sms_id}
                msg={msg}
                deletePhone={deletePhone}
                />)}  
            </tbody>
        </table>
       </div>
    )
}

export default Home
