import { useState } from "react"

const RegisterMessage = () => {
    const [data, setData] = useState({
        msg_phone: '',
        status: ''
    })

    const handleChange = (e) => {
        const { name, value } = e.target

        setData(data => ({
            ...data,
            [name]: value
        }))
    }

    const createMessage = async (e) => {
        e.preventDefault()

        try {
            const res = await fetch(`${process.env.REACT_APP_SERVERURL}/update-status`, {
                method: "POST",
                headers: {
                    "Content-Type":"application/json"
                },
                body: JSON.stringify(data)
            })
        } catch (err) {
            console.log(err)
        }
    }

    return(
        <section>
            <h1 className="text-center mt-2">Register Message</h1>
            <form onSubmit={createMessage}>
                <div className="form-group">
                  <label htmlFor="msg_phone">Message</label>
                   <textarea
                   value={data.msg_phone}
                   name="msg_phone"
                   id="msg_phone"
                   className="form-control"
                   placeholder="Type your message"
                   onChange={handleChange}
                   />
                </div>
                <div className="form-group">
                    <label htmlFor="status">Status</label>
                    <select name="status" id="status" className="form-control" value={data.status} onChange={handleChange}>

                    </select>
                </div>
            </form>
        </section>
    )
}

export default RegisterMessage
