import { useParams, useNavigate } from "react-router-dom"
import { useState, useEffect } from "react"

const Edit = () => {
    const [data, setData] = useState({
        phone: '',
        message: '',
        status: ''
    })
    const navigate = useNavigate()
    const { id } = useParams()

    const handleChange = (e) => {
        const { name, value } = e.target

        setData(data => ({
            ...data,
            [name]: value
        }))
    }

    const updateMessage = async () => {
        console.log('yo')
    }

    useEffect(() => {
        const getById = async () => {
            try {
                const res = await fetch(`${process.env.REACT_APP_SERVERURL}/message/${id}`)
                const json = await res.json()
                setData(json.phone_message)
            } catch (err) {
                console.log(err)
            }
        }
        getById()
    }, [])

    return(
        <section>
            <h1 className="mt-2 text-center">Edit</h1>
            <div className="row">
                <div className="col-md-6 mx-auto">
                    <form onSubmit={updateMessage}>
                        <div className="form-group">
                            <label htmlFor="phone">Celular</label>
                            <input
                            type="text"
                            className="form-control"
                            name="phone"
                            id="phone"
                            placeholder="Ex: (DDD)00000-0000"
                            value={data.phone}
                            onChange={handleChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="msg">Message</label>
                            <textarea
                            className="form-control m-2"
                            name="message"
                            id="msg"
                            value={data.message}
                            placeholder="Escreva sua mensagem"
                            onChange={handleChange}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="status">Status</label>
                            <select name="status" id="status" value={data.status} onChange={handleChange} className="form-control">
                                <option defaultChecked>Status</option>
                                <option value="enviado">Enviado</option>
                                <option value="recebido">Recebido</option>
                                <option value="erro de envio">Erro de Envio</option>
                            </select>
                        </div>
                        <button className="btn btn-dark" type="submit">Update</button>
                    </form>
                </div>
            </div>
        </section>
    )
}

export default Edit
