import { useParams } from "react-router-dom"
import { useState, useEffect } from "react"

const Edit = () => {
    const [data, setData] = useState({
        msg_phone: ''
    })
    // const navigate = useNavigate()
    const { id } = useParams()

    const handleChange = (e) => {
        const { name, value } = e.target

        setData(data => ({
            ...data,
            [name]: value
        }))
    }

    const updateMessage = async (e) => {
        e.preventDefault()
        try {
            const res = await fetch(`${process.env.REACT_APP_SERVERURL}/message/${id}`, {
                method: "PUT",
                headers: {
                    "Content-Type":"application/json"
                },
                body: JSON.stringify(data)
            })
            console.log(res)
        } catch (err) {
            console.log(err)

        }
    }

    useEffect(() => {
        const getById = async () => {
            try {
                const res = await fetch(`${process.env.REACT_APP_SERVERURL}/update/${id}`)
                const json = await res.json()
                setData(json.update_message)
            } catch (err) {
                console.log(err)
            }
        }
        getById()
    }, [id])

    return(
        <section>
            <h1 className="mt-2 text-center">Edit</h1>
            <div className="row">
                <div className="col-md-6 mx-auto">
                    <form onSubmit={e => updateMessage(e)}>
                        <div className="form-group">
                            <label htmlFor="msg_phone">Celular</label>
                            <input
                            type="text"
                            className="form-control"
                            name="msg_phone"
                            id="msg_phone"
                            placeholder="Ex: (DDD)00000-0000"
                            value={data.msg_phone}
                            onChange={handleChange}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="sms_msg">Message</label>
                            <textarea
                            className="form-control m-2"
                            name="sms_msg"
                            id="sms_msg"
                            value={data.sms_msg}
                            placeholder="Escreva sua mensagem"
                            onChange={handleChange}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="status">Status</label>
                            <select name="status" id="status" value={data.status} onChange={handleChange} className="form-control">
                                <option defaultChecked>Status</option>
                                <option value="enviado">Enviado</option>
                                <option value="recebido">Recebido</option>
                                <option value="erro de envio">Erro de Envio</option>
                            </select>
                        </div>
                        <button className="btn btn-dark" type="submit">Update</button>
                    </form>
                </div>
            </div>
        </section>
    )
}

export default Edit
